import React, { useState, useMemo, useCallback } from 'react';
import menuData from '../data/menuData.json';

const Menu = () => {
  const [activeCategory, setActiveCategory] = useState('breakfast');
  const [selectedItem, setSelectedItem] = useState(null);

  const categories = [
    { key: 'breakfast', label: 'Breakfast' },
    { key: 'lunch', label: 'Lunch & Dinner' },
    { key: 'beverages', label: 'Coffee & Tea' },
    { key: 'special', label: 'Our Authentic Food' }
  ];

  const filteredMenu = useMemo(() => {
    return menuData[activeCategory] || [];
  }, [activeCategory]);

  const handleCategoryChange = useCallback((categoryKey) => {
    setActiveCategory(categoryKey);
  }, []);

  const handleItemClick = useCallback((item) => {
    setSelectedItem(item);
  }, []);

  const closeModal = useCallback(() => {
    setSelectedItem(null);
  }, []);

  return (
    <section id="menu" className="py-16 px-4" style={{ backgroundColor: '#4b2c2c' }} aria-labelledby="menu-heading">
      <div className="max-w-7xl mx-auto">
        <div className="text-center mb-12">
          <h2 id="menu-heading" className="text-4xl md:text-5xl font-bold mb-6" style={{ color: '#f4d7a1', fontFamily: 'Georgia, serif' }}>
            Our Menu
          </h2>
          <p className="text-lg md:text-xl max-w-3xl mx-auto" style={{ color: '#f4d7a1' }}>
            Fresh ingredients, delicious meals. We have one of the most delicious breakfast, 
            lunch, dinner & coffee menus in Woodside, Queens. Don't miss our authentic food!
          </p>
        </div>


        {/* Category Tabs */}
        <div className="flex flex-wrap justify-center gap-4 mb-12">
          {categories.map((category) => (
            <button
              key={category.key}
              onClick={() => handleCategoryChange(category.key)}
              className={`px-6 py-3 rounded-lg font-medium transition-all duration-300 ${
                activeCategory === category.key
                  ? 'bg-yellow-600 text-white shadow-lg scale-105'
                  : 'bg-yellow-100 text-yellow-800 hover:bg-yellow-200 hover:text-yellow-900 shadow-md hover:shadow-lg'
              }`}
              style={{ fontFamily: 'Georgia, serif' }}
              aria-pressed={activeCategory === category.key}
              aria-label={`Filter by ${category.label}`}
            >
              {category.label}
            </button>
          ))}
        </div>

        {/* Special Title for Our Authentic Food */}
        {activeCategory === 'special' && (
          <div className="text-center mb-12">
            <h3 className="text-3xl md:text-4xl font-bold mb-4" style={{ color: '#f4d7a1', fontFamily: 'Georgia, serif' }}>
              Our Organic and Health
            </h3>
            <p className="text-lg md:text-xl max-w-2xl mx-auto italic" style={{ color: '#f4d7a1' }}>
              Representing our Nepal's culture
            </p>
          </div>
        )}


        {/* Menu Items Grid */}
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
          {filteredMenu.length > 0 ? (
            filteredMenu.map((item) => (
              <div
                key={item.id}
                className="bg-white/10 backdrop-blur-sm rounded-lg p-6 hover:bg-white/20 transition-all duration-300 transform hover:-translate-y-2 cursor-pointer group"
                onClick={() => handleItemClick(item)}
                style={{ fontFamily: 'Georgia, serif' }}
              >
                <h3 className="text-xl font-bold mb-3 group-hover:text-yellow-300 transition-colors duration-300" style={{ color: '#f4d7a1' }}>
                  {item.name}
                </h3>
                <p className="text-lg font-semibold" style={{ color: '#f4d7a1' }}>
                  {item.price}
                </p>
                <div className="mt-4 text-sm opacity-75" style={{ color: '#f4d7a1' }}>
                  Click to view image
                </div>
              </div>
            ))
          ) : (
            <div className="col-span-full text-center py-12">
              <div className="mb-4" style={{ color: '#f4d7a1' }}>
                <svg className="w-16 h-16 mx-auto" fill="none" stroke="currentColor" viewBox="0 0 24 24" aria-hidden="true">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={1} d="M9.172 16.172a4 4 0 015.656 0M9 12h6m-6-4h6m2 5.291A7.962 7.962 0 0112 15c-2.34 0-4.29-1.009-5.824-2.709M15 12a3 3 0 11-6 0 3 3 0 016 0z" />
                </svg>
              </div>
              <h3 className="text-xl font-semibold mb-2" style={{ color: '#f4d7a1', fontFamily: 'Georgia, serif' }}>No items found</h3>
              <p className="text-lg" style={{ color: '#f4d7a1' }}>
                No items available in this category
              </p>
            </div>
          )}
        </div>

        {/* Call to Action */}
        <div className="text-center mt-16">
          <p className="text-lg md:text-xl mb-6" style={{ color: '#f4d7a1' }}>
            Can't decide? Come visit us and let our friendly staff help you choose!
          </p>
          <button 
            onClick={() => {
              const element = document.getElementById('contact');
              if (element) {
                element.scrollIntoView({ behavior: 'smooth' });
              }
            }}
            className="bg-yellow-600 hover:bg-yellow-700 text-white px-8 py-3 rounded-lg font-medium transition-all duration-300 hover:scale-105 shadow-lg"
            style={{ fontFamily: 'Georgia, serif' }}
            aria-label="Visit our cafe location"
          >
            Visit Us
          </button>
        </div>

        {/* Image Modal */}
        {selectedItem && (
          <div 
            className="fixed inset-0 bg-black bg-opacity-75 flex items-center justify-center z-50 p-4"
            onClick={closeModal}
          >
            <div className="relative max-w-4xl max-h-full">
              <button
                onClick={closeModal}
                className="absolute -top-12 right-0 text-white hover:text-yellow-300 text-4xl font-bold transition-colors duration-200 z-10"
                aria-label="Close modal"
              >
                Ã—
              </button>
              <img
                src={selectedItem.image}
                alt={`${selectedItem.name} - ${selectedItem.description}`}
                className="max-w-full max-h-full object-contain rounded-lg"
                style={{ maxWidth: '80vw', maxHeight: '80vh' }}
              />
              <div className="absolute bottom-0 left-0 right-0 bg-black bg-opacity-50 text-white p-4 rounded-b-lg">
                <h3 className="text-xl font-bold mb-2" style={{ fontFamily: 'Georgia, serif' }}>
                  {selectedItem.name}
                </h3>
                <p className="text-lg font-semibold mb-2">
                  {selectedItem.price}
                </p>
                <p className="text-sm opacity-90">
                  {selectedItem.description}
                </p>
              </div>
            </div>
          </div>
        )}
      </div>
    </section>
  );
};

export default Menu;
