import React, { useState, useEffect } from 'react';

const Navbar = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [isScrolled, setIsScrolled] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      const scrollTop = window.scrollY;
      setIsScrolled(scrollTop > 100);
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const scrollToSection = (sectionId) => {
    const element = document.getElementById(sectionId);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
    setIsMenuOpen(false);
  };

  return (
    <nav className={`fixed w-full top-0 z-50 transition-all duration-500 ${
      isScrolled 
        ? 'bg-white/95 backdrop-blur-md shadow-lg py-2' 
        : 'bg-transparent py-4 md:py-6'
    }`}>
      <div className="container-custom px-4">
        <div className="flex justify-between items-center">
          {/* Logo */}
          <div className={`flex items-center transition-all duration-500 ${
            isScrolled 
              ? 'justify-start' 
              : 'justify-center absolute left-1/2 transform -translate-x-1/2'
          }`}>
            <img 
              src="/maxresdefault.jpg" 
              alt="Manaslu Cafe Logo" 
              className={`rounded-lg transition-all duration-500 ${
                isScrolled 
                  ? 'h-8 w-auto md:h-10' 
                  : 'h-12 w-auto md:h-16'
              }`}
            />
          </div>

          {/* Desktop Menu */}
          <div className={`hidden md:flex space-x-8 transition-all duration-500 ${
            isScrolled ? 'opacity-100' : 'opacity-0 pointer-events-none'
          }`}>
            <button
              onClick={() => scrollToSection('home')}
              className="text-gray-700 hover:text-cafe-brown transition-colors duration-300 font-medium text-sm"
            >
              Home
            </button>
            <button
              onClick={() => scrollToSection('menu')}
              className="text-gray-700 hover:text-cafe-brown transition-colors duration-300 font-medium text-sm"
            >
              Menu
            </button>
            <button
              onClick={() => scrollToSection('contact')}
              className="text-gray-700 hover:text-cafe-brown transition-colors duration-300 font-medium text-sm"
            >
              Contact
            </button>
          </div>

          {/* Mobile Menu Button */}
          <div className="md:hidden">
            <button
              onClick={toggleMenu}
              className={`transition-colors duration-300 ${
                isScrolled 
                  ? 'text-gray-700 hover:text-cafe-brown' 
                  : 'text-white hover:text-cafe-gold'
              } focus:outline-none`}
            >
              <svg className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                {isMenuOpen ? (
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                ) : (
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 12h16M4 18h16" />
                )}
              </svg>
            </button>
          </div>
        </div>

        {/* Mobile Menu */}
        {isMenuOpen && (
          <div className="md:hidden bg-white/95 backdrop-blur-md border-t mt-2 rounded-lg shadow-lg">
            <div className="px-4 pt-4 pb-6 space-y-2">
              <button
                onClick={() => scrollToSection('home')}
                className="block px-4 py-3 text-gray-700 hover:text-cafe-brown hover:bg-cafe-cream rounded-lg w-full text-left transition-all duration-300 font-medium"
              >
                Home
              </button>
              <button
                onClick={() => scrollToSection('menu')}
                className="block px-4 py-3 text-gray-700 hover:text-cafe-brown hover:bg-cafe-cream rounded-lg w-full text-left transition-all duration-300 font-medium"
              >
                Menu
              </button>
              <button
                onClick={() => scrollToSection('contact')}
                className="block px-4 py-3 text-gray-700 hover:text-cafe-brown hover:bg-cafe-cream rounded-lg w-full text-left transition-all duration-300 font-medium"
              >
                Contact
              </button>
            </div>
          </div>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
